mapper.select:
SELECT * FROM spring5fs.member


	and email like '%${email}%'


	and name = #{name}


	and regdate between #{from} and #{to}


ORDER BY regdate DESC

mapper.update:
UPDATE spring5fs.member SET
email=#{email}, password=#{password}, name=#{name}, introduce=#{introduce}

	, profile=#{profile}

WHERE id= #{id}
